import React, { useState } from 'react'
import { useTimer } from "react-timer-hook";
import "./vehicle.css"
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';

export default function BasicAlerts() {
    return (
        <Stack sx={{ width: '100%' }} spacing={2}>
            <Alert severity="error">This is an error alert — check it out!</Alert>
            <Alert severity="warning">This is a warning alert — check it out!</Alert>
            <Alert severity="info">This is an info alert — check it out!</Alert>
            <Alert severity="success">This is a success alert — check it out!</Alert>
        </Stack>
    );
}

export const SingleVehicle = (props) => {

    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [book, setbook] = useState("book")

    const {
        seconds,
        minutes,
        restart,
    } = useTimer({

        onExpire: () => setbook("expired")
    });

    return (
        <div className="singleVehicleDetaile">
            <img src={props.image} alt="" />
            <h2>{props.name}</h2>
            <p>{props.type}</p>
            {book === "book" ? (

                <button onClick={() => {
                    // Restarts to 2 minutes timer
                    const time = new Date();
                    time.setSeconds(time.getSeconds() + 10);
                    setbook("expired");
                    restart(time);
                    setbook("pick");


                }}>
                    Book
                </button>

            ) : book === "pick" ?
                (
                    <>
                        <div style={{ fontSize: "38px" }}>
                            <span>{minutes}</span>:<span>{seconds}</span>
                        </div>
                        <button onClick={() => setbook("booked")}>Pick</button>

                    </>
                ) : book === "booked" ? (
                    <>
                        <p>You can travel maximum 10 kms and put it in Outlets</p>
                        <button style={{ backgroundColor: "green" }}>Booked</button>
                    </>
                ) : book === "expired" ? (
                    <>
                        <Stack sx={{ width: '100%' }} spacing={2}>
                            <Alert severity="error">Expired The time!!! you cannot book now!</Alert>
                        </Stack>
                        {/* <p>Expired The time!!! you cannot book now.</p> */}
                    </>
                ) : (
                    <div></div>
                )
            }
        </div>
    )
}
