import React, { useState } from 'react'
// import ReactDOM from "react-dom";
import { useTimer } from "react-timer-hook";
import "./vehicle.css"

let minutes = 1
let seconds = 60

export const SingleVehicle = (props) => {

    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [book, setbook] = useState("book")
    const [[mins, secs], setTime] = useState([minutes, seconds]);

    // const MyTimer=({ expiryTimestamp }) => {
    //     const {
    //         seconds,
    //         minutes,
    //     } = useTimer({
    //         expiryTimestamp,
    //         onExpire: () => alert("onExpire called")
    //     });

        const tick = () => {

            if (mins === 0 && secs === 0)
                reset()
            else if (secs === 0) {
                setTime([mins - 1, 59]);
            } else {
                setTime([mins, secs - 1]);
            }
        };

        const reset = () => setTime([parseInt(minutes), parseInt(seconds)]);


        // useEffect(() => {
        //     const timerId = setInterval(() => tick(), 1000);
        //     return () => clearInterval(timerId);
        // });


        return (
            <div className="eachVehicleDetaile">
                <img src={props.image} alt="" />
                <h2>{props.name}</h2>
                <p>{props.type}</p>
                {book === "book" ? (
                    <button onClick={() => setbook("pick")}>Book</button>
                ) : book === "pick" ?
                    (
                        <>
                            {/* <div style={{ fontSize: "100px" }}>
                                <span>{minutes}</span>:<span>{seconds}</span>
                            </div> */}
                            <p>{`${mins
                                .toString()
                                .padStart(2, '0')}:${secs.toString().padStart(2, '0')}`}</p>
                            <button onClick={() => setbook("booked")}>Pick</button>
                            {/* <button
                    onClick={() => {
                        // Restarts to 5 minutes timer
                        var t = new Date();
                        t.setSeconds(t.getSeconds() + 10);
                        <MyTimer expiryTimestamp={t + "booked"} />
                    }}
                >
                    Pick
                </button> */}
                        </>
                    ) : book === "booked" ? (
                        <>
                            <p>You can travel max 10 kms and put it in Bus-Stop Station</p>
                            <button style={{ backgroundColor: "green" }}>Booked</button>
                        </>
                    ) : (
                        <div></div>
                    )}
            </div>
        )
    }
