import React, { useState } from 'react'
import { useTimer } from "react-timer-hook";
import "./vehicle.css"
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';


export const SingleVehicle = (props, { expiryTimestamp }) => {

    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [book, setbook] = useState("book")

    const {
        seconds,
        minutes,
        pause,
        restart,
    } = useTimer({
        expiryTimestamp,

        onExpire: () => expire(setbook("error"))

    });

    const expire = () => {
        if (minutes + seconds <= 0) {
            console.log("Time Expired");
        }
    }
    return (
        <div className="singleVehicleDetaile">
            <img src={props.image} alt="" />
            <h2>{props.name}</h2>
            <p>{props.type}</p>
            {book === "book" ? (

                <button onClick={() => {
                    // Restarts to 2 minutes timer
                    const time = new Date();
                    const value = time.setSeconds(time.getSeconds() + 10);
                    restart(value);
                    setbook("pick")
                }}>
                    Book
                </button>

            ) : book === "pick" ?
                (
                    <>
                        <div style={{ fontSize: "38px" }}>
                            <span>{minutes}</span>:<span>{seconds}</span>
                        </div>

                        <button onClick={() =>

                            pause(setbook("booked"))}

                        >Pick</button>


                    </>
                ) : book === "booked" ? (
                    <>

                        <Stack sx={{ width: '100%' }} spacing={2}>
                            <Alert severity="success">You can travel maximum 10 kms and put it in Outlets!</Alert>
                        </Stack><br />
                        <button style={{ backgroundColor: "green" }}>Booked</button>
                        <div>
                            {buttons}
                            <Snackbar
                                anchorOrigin={{ vertical, horizontal }}
                                open={open}
                                onClose={handleClose}
                                message="Booked Successfully!"
                                key={vertical + horizontal}
                            />
                        </div>
                    </>
                ) : book === "error" ? (
                    <>
                        <Stack sx={{ width: '100%' }} spacing={2}>
                            <Alert severity="error">Expired The time!!! you cannot book now!</Alert>
                        </Stack>

                    </>
                ) : (
                    <div></div>
                )
            }
        </div>
    )
}
